components/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}

components/common/share-links:
{{!--
  Share Links - Displayed on Blog Posts and Product Pages

  @param url
    The URL of the post that should be shared to social media.

  @param title
    The title / name to display when sharing

  @param share_buttons
    The data for the buttons to be shared from Bigcommerce

  @param share_type (optional)
    Added as a class name to the share-links-container

  @param show_heading (optional)
    Whether to show the heading text "Share this: "

  @param image
    The image to add to the pinterest post
--}}

{{#if share_buttons.buttons.length}}
  <aside class="share-links-container type-{{share_type}}{{#any settings.add_this.buttons service='facebook_like'}} has-like{{/any}}">
    {{#if show_heading}}
      <div class="share-links-label">{{lang 'common.share'}}</div>
    {{/if}}

    <ul class="share-links">
      {{#each share_buttons.buttons}}

        {{#if service '===' 'facebook'}}
          <li class="share-link">
            <a target="_blank" href="//www.facebook.com/sharer.php?u={{../../url}}" class="share-facebook" data-share>
              <span class="visually-hidden">Facebook</span>
              {{> svg/social/facebook}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'twitter'}}
          <li class="share-link">
            <a target="_blank" href="//twitter.com/share?url={{../../url}}" class="share-twitter" data-share>
              <span class="visually-hidden">Twitter</span>
              {{> svg/social/twitter}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'pinterest'}}
          <li class="share-link">
            <a target="_blank" class="share-pinterest" href="//pinterest.com/pin/create/button/?url={{../../url}}&amp;media={{../../image}}&amp;description={{../../title}}" data-share>
              <span class="visually-hidden">Pinterest</span>
              {{> svg/social/pinterest}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'linkedin'}}
          <li class="share-link">
            <a target="_blank" href="//www.linkedin.com/cws/share?url={{../../url}}" class="share-linkedin" data-share>
              <span class="visually-hidden">LinkedIn</span>
              {{> svg/social/linkedin}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'google_plusone'}}
          <li class="share-link">
            <a target="_blank" href="//plus.google.com/share?url={{../../url}}" class="share-google" data-share>
              <span class="visually-hidden">Google Plus</span>
              {{> svg/social/googleplus}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'email'}}
          <li class="share-link">
            <a href="mailto:?subject={{../../title}}&amp;body={{../../url}}" class="share-email">
              <span class="visually-hidden">{{lang 'common.email'}}</span>
              {{> svg/social/email}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'print'}}
          <li class="share-link">
            <a class="share-print" data-share-print>
              <span class="visually-hidden">{{lang 'common.print'}}</span>
              {{> svg/social/print}}
            </a>
          </li>
        {{/if}}

      {{/each}}
      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook_like'}}
          <li class="share-link facebook-like">
            <div class="fb-like"
              data-href="{{../../url}}"
              data-layout="button_count"
              data-action="like"
              data-show-faces="false">
            </div>
          </li>
        {{/if}}
      {{/each}}
    </ul>
  </aside>
{{/if}}

components/product/add-to-cart-form:
{{!--
  Add To Cart Form -- As displayed within a full product page.
--}}

<form
  class="form add-to-cart-form{{#unless quickshop}}-full{{/unless}}"
  method="post"
  action="{{product.cart_url}}"
  enctype="multipart/form-data"
  data-product-options-count="{{product.options.length}}"
  data-cart-item-add>
  <input type="hidden" name="action" value="add">
  <input type="hidden" name="product_id" value="{{product.id}}" data-product-id>

  <div data-product-option-change>
    {{#if product.options}}
      {{#each product.options}}
        {{{dynamicComponent 'core/forms/options'}}}
      {{/each}}
    {{/if}}

    {{#if product.customizations}}
      {{#each product.customizations}}
        {{{dynamicComponent 'core/forms/customizations'}}}
      {{/each}}
    {{/if}}

    {{#if product.event_date}}
      {{> core/products/event}}
    {{/if}}

    {{#if product.can_purchase}}
      {{> components/common/quantity product}}
    {{/if}}
  </div>

  <div class="product-cart-messages">
    <div data-product-cart-message>{{!-- Add to cart alerts --}}</div>
    {{#if product.can_purchase}}
      <div data-product-option-message>{{!-- Product option change alerts --}}</div>
    {{/if}}
  </div>

  <div class="form-field add-to-cart-buttons" data-product-add>

    {{#if product.can_purchase}}
      {{#if product.pre_order}}
        <input class="button button-inverted" type="submit" value="{{lang 'product.pre_order'}}" data-button-purchase data-button-preorder>
      {{else}}
        <input class="button button-inverted" type="submit" value="{{lang 'product.add_to_cart'}}" data-button-purchase>
      {{/if}}
    {{else}}
      {{#if product.out_of_stock}}
        <div class="button button-inverted button-disabled">
          {{lang 'product.sold_out'}}
        </div>
      {{/if}}
    {{/if}}

    {{#if settings.show_wishlist}}
      <a class="button button-inverted button-square" href="{{product.add_to_wishlist_url}}">{{> svg/heart}}</a>
      <span class="spinner"></span>
    {{/if}}

  </div>
</form>

components/product/price:
{{!
  Product Price - Sale vs Normal, With Tax vs Without

  @param this price object

  @param show_savings bool (optional)
         If true, show a label saying how much is saved from the full price.

  @param schema_org bool (optional)
         If false, will keep the schema.org meta data out of this price.
}}
{{#if price}}

  <span class="price {{> components/common/price-class}}"
    itemprop="offers"
    itemscope
    itemtype="http://schema.org/Offer">

    {{#if schema_org}}
      <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
      {{#if price.without_tax}}
        <meta itemprop="price" content="{{price.without_tax.value}}">
      {{else}}
        <meta itemprop="price" content="{{price.with_tax.value}}">
      {{/if}}
    {{/if}}

    {{#if price.with_tax}}
      <span class="price-withtax" data-price="with-tax">
        <span class="price-value" data-price-value="with-tax">
          {{price.with_tax.formatted}}
        </span>
        {{#if price.rrp_with_tax}}
          <span class="price-rrp" data-price-rrp="with-tax">{{price.rrp_with_tax.formatted}}</span>
        {{/if}}
        {{#if price.without_tax}}
          <span class="price-tax-label">{{lang 'product.including_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}

    {{#if price.without_tax}}
      <span class="price-withouttax" data-price="without-tax">
        <span class="price-value" data-price-value="without-tax">
          {{price.without_tax.formatted}}
        </span>
        {{#if price.rrp_without_tax}}
          <span class="price-rrp" data-price-rrp="without-tax">{{price.rrp_without_tax.formatted}}</span>
        {{/if}}
        {{#if price.with_tax}}
          <span class="price-tax-label">{{lang 'product.excluding_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}

    {{#if show_savings}}
      {{#if price.saved}}
        <span class="price-saved" data-price-saved>
          ({{lang 'product.you_save'}} {{price.saved.formatted}})
        </span>
      {{/if}}
    {{/if}}

  </span>
{{/if}}
