components/blog/post-sidebar:
{{!--
  Sidebar displayed for blog posts.
--}}

<aside class="page-sidebar">

  {{#if blog.recent_posts}}
    <nav class="sidebar-block">
      <h2 class="blog-block-title">Recent Posts</h2>
      <ul>
        {{#each blog.recent_posts}}
          <li><a href="{{url}}">{{title}}</a></li>
        {{/each}}
      </ul>
    </nav>
  {{/if}}

  {{! all the blog tags -- not currently possible}}
</aside>
