components/faceted-search/facet-block:
{{!--
  Displays a facet block in the sidebar of product-listing pages
  and includes toggling facet controls open / closed
--}}

{{#if visible}}
  <div class="sidebar-block facet-{{toLowerCase facet}} {{#unless items.length}}no-items{{/unless}}" data-facet-filter>

    <h5 class="facet-block-title" data-facet-toggle>
      {{title}}

      {{#if start_collapsed}}
        <span>&#43;</span>
      {{else}}
        <span>&ndash;</span>
      {{/if}}
    </h5>

    {{{dynamicComponent 'components/faceted-search/facets'}}}

    {{#if remove_url}}
      <a class="facet-remove" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>{{lang 'faceted_search.clear'}}</a>
    {{/if}}

  </div>
{{/if}}

components/reviews/ratings:
{{!--
  Display Rating as Series of Stars

  @param rating
    The Rating value to display.
--}}

<span class="rating-stars" aria-hidden="true">
  {{#for 1 5}}
    {{#if ../rating '>=' $index}}
      <svg class="star star-full">
        <use xlink:href="#icon-star-full">
      </svg>
    {{else}}
      <svg class="star star-empty">
        <use xlink:href="#icon-star">
      </svg>
    {{/if}}
  {{/for}}
</span>
