components/product-lists/sidebar:
{{!--
  Product List - Sidebar
  Displays a series of blocks with search facet options.

  @param TODO: fill this in - needs to be passed the data from the page I think

  @param listtype   The category object for list, search, brand, etc
--}}

<aside class="product-list-sidebar">

  {{!> components/product-lists/product-count ... }}
  {{! Not Possible Yet: "Showing 1 - 9 of 75" TODO when possible.}}

  {{#if listtype.faceted_search_enabled}}
    <div class="faceted-search-blocks" data-faceted-search>
      {{#each listtype.facets}}
        {{> components/faceted-search/facet-block}}
      {{/each}}
    </div>
  {{else}}
    <div class="faceted-search-blocks">
      {{> components/product-lists/subcategories listtype}}
      {{#if theme_settings.show_shop_by_price}}
        {{> components/product-lists/shop-by-price listtype.shop_by_price}}
      {{/if}}
      {{#if theme_settings.show_shop_by_brand}}
        {{> components/product-lists/shop-by-brand shop_by_brand}}
      {{/if}}
    </div>
  {{/if}}

</aside>

components/faceted-search/facet-block:
{{!--
  Displays a facet block in the sidebar of product-listing pages
  and includes toggling facet controls open / closed
--}}

{{#if visible}}
  <div class="sidebar-block facet-{{toLowerCase facet}} {{#unless items.length}}no-items{{/unless}}" data-facet-filter>

    <h5 class="facet-block-title" data-facet-toggle>
      {{title}}

      {{#if start_collapsed}}
        <span>&#43;</span>
      {{else}}
        <span>&ndash;</span>
      {{/if}}
    </h5>

    {{{dynamicComponent 'components/faceted-search/facets'}}}

    {{#if remove_url}}
      <a class="facet-remove" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>{{lang 'faceted_search.clear'}}</a>
    {{/if}}

  </div>
{{/if}}

components/product-lists/subcategories:
{{!--
  Subcategories Block - Shows the product subcategories in the sidebar.
--}}

{{#if subcategories}}
  <div class="sidebar-block subcategories-block">
    <h3 class="facet-block-title">{{lang 'category.title'}}</h3>
    <ul class="sidebar-categories">
      {{#each subcategories}}
        <li class="subcategory">
          <a href="{{url}}">{{{name}}}</a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}

components/product-lists/shop-by-price:
{{!--
  Shop by Price Sidebar block
  Shows a list of price ranges to narrow results

  @param this   listtype.shop_by_price
    The "shop_by_price" object from categories / search / etc
--}}

{{#if this.length}}
  <nav class="sidebar-block">
    <h2 class="sidebar-title">{{lang 'unfaceted_search.shop_by_price'}}</h2>
    <ul class="shop-by-price-list">
      {{#each this}}
        <li class="shop-by-price-item">
          <a class="shop-by-price-action {{#if selected}}active{{/if}}" href="{{url}}"
            title="{{low.formatted}} - {{high.formatted}}">{{low.formatted}} - {{high.formatted}}
          </a>
        </li>
      {{/each}}
    </ul>
  </nav>
{{/if}}

components/product-lists/shop-by-brand:
{{!--
  Shop by Brand Sidebar block
  Shows a list of brands to select from

  @param this   shop_by_brand
    The "shop_by_price" object from categories / search / etc
--}}

{{#if this.length}}
  <nav class="sidebar-block">
    <h2 class="sidebar-title">{{lang 'unfaceted_search.shop_by_brand'}}</h2>
    <ul class="shop-by-brand-list">
      {{#each this}}
        <li class="shop-by-brand-item">
          <a class="shop-by-brand-action {{#if selected}}active{{/if}}" href="{{url}}"
            title="{{name}}">{{name}}
          </a>
        </li>
      {{/each}}
    </ul>
  </nav>
{{/if}}

components/reviews/ratings:
{{!--
  Display Rating as Series of Stars

  @param rating
    The Rating value to display.
--}}

<span class="rating-stars" aria-hidden="true">
  {{#for 1 5}}
    {{#if ../rating '>=' $index}}
      <svg class="star star-full">
        <use xlink:href="#icon-star-full">
      </svg>
    {{else}}
      <svg class="star star-empty">
        <use xlink:href="#icon-star">
      </svg>
    {{/if}}
  {{/for}}
</span>
