components/common/pagination:
{{!--
  Pagination: Previous / Next buttons and a list of links to other pages.
  @param this Object containing links, count, etc
--}}
<nav class="pagination">
  {{#if previous}}
    <a class="pagination-item pagination-previous" href="{{previous}}" data-faceted-search-facet>
      <span class="visually-hidden">{{lang 'common.pagination.previous'}}</span>
    </a>
  {{/if}}

  {{#each links}}
    <a
      class="pagination-item{{#if number "==" ../current}} pagination-current{{/if}}"
      href="{{url}}"
      data-faceted-search-facet>
      {{number}}
    </a>
  {{/each}}

  {{#if next}}
    <a class="pagination-item pagination-next" href="{{next}}" data-faceted-search-facet>
      <span class="visually-hidden">{{lang 'common.pagination.next'}}</span>
    </a>
  {{/if}}
</nav>

components/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}

components/faceted-search/facet-block:
{{!--
  Displays a facet block in the sidebar of product-listing pages
  and includes toggling facet controls open / closed
--}}

{{#if visible}}
  <div class="sidebar-block facet-{{toLowerCase facet}} {{#unless items.length}}no-items{{/unless}}" data-facet-filter>

    <h5 class="facet-block-title" data-facet-toggle>
      {{title}}

      {{#if start_collapsed}}
        <span>&#43;</span>
      {{else}}
        <span>&ndash;</span>
      {{/if}}
    </h5>

    {{{dynamicComponent 'components/faceted-search/facets'}}}

    {{#if remove_url}}
      <a class="facet-remove" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>{{lang 'faceted_search.clear'}}</a>
    {{/if}}

  </div>
{{/if}}

components/other-search-results:
{{!-- Showing individual blog and page results on the search page --}}

{{#each content_results}}
  <article class="search-result content-result">
    <h3 class="search-result-title"><a href="{{url}}">{{title}}</a></h3>
    <p>{{content}}</p>
    <p class="content-result-type">{{type}}</p>
  </article>
{{/each}}

{{> components/common/pagination pagination.content_results}}

components/product-lists/display:
{{!--
  Product List Display - A wrapper around different types of product lists
  Displays a list of products (category / brand / search)

  @param listtype       The section this display applies to.
  @param pagination     The pagination object related to this view.
  @param quickshop      Whether to show the quickshop button.

--}}

{{! Product Items -- displayed as grid / list}}
<div class="product-list-content">

  {{!-- TODO: Add options to display list items instead of grid (or just remove this block)
    {{#if theme_settings.product_list_display_mode '===' 'grid'}}
      {{> components/product-lists/grid
        products=listtype.products
        compare=listtype.show_compare
      }}
    {{else}}
      {{> components/product-lists/list
        products=listtype.products
        compare=listtype.show_compare
      }}
    {{/if}}
  --}}

  {{> components/product-lists/grid
    products=listtype.products
    quickshop=quickshop
    compare=listtype.show_compare
  }}

  {{> components/common/pagination pagination}}
</div>

components/product-lists/grid:
{{!--
  Product List Grid - Displays products in a grid like fashion.

  @param products
    The list of product items to loop over / display.
--}}

{{#if products.length}}

  <ul class="product-grid">
    {{#each products}}
      <li class="product-grid-item">
        {{> components/product/product-grid-item
          image-ratio=../theme_settings.image_ratio_grid
          default-image=../theme_settings.default_image
          compare=../compare
          quickshop=../quickshop
        }}
      </li>
    {{/each}}
  </ul>

{{else}}
  <div class="contained contained-narrow">
    <p>{{lang 'search.results.no_results'}}</p>
    {{> components/search-box}}
  </div>
{{/if}}

components/product-lists/index:
{{!--
  Product List -- Index

  Wrapper around the product-list content that takes care of assigning the
  values that the child templates will need. This is the template to call
  via ajax when updating product list content.

  # Summary of Component Tree:

  Base Page: Category / Brand / Search
    Product-List Index (switch / ajax content root)
      Display (sidebar/paginate etc)
        List or Grid
          Product Items

--}}

{{#if category}}
  {{{snippet 'categories'}}}

  {{> components/product-lists/category-header category=category}}

  {{> components/product-lists/list-utils
    listtype=category
    pagination=pagination.category
  }}

  {{> components/product-lists/display
    listtype=category
    pagination=pagination.category
    quickshop=theme_settings.use_quickshop
  }}
{{/if}}

{{#if brand}}
  {{{snippet 'brand'}}}

  {{> components/product-lists/category-header category=brand}}

  {{> components/product-lists/list-utils
    listtype=brand
    pagination=pagination.brand
  }}

  {{> components/product-lists/display
    listtype=brand
    pagination=pagination.brand
    quickshop=theme_settings.use_quickshop
  }}
{{/if}}

{{#if product_results}}
  {{{snippet 'search'}}}

  <div class="search-tabs-nav">
    <button class="search-tab-toggle active" data-search-tab-toggle="products">{{lang 'search.results.tab_products'}} {{pagination.product_results.total}}</button>
    <button class="search-tab-toggle" data-search-tab-toggle="other">{{lang 'search.results.tab_other'}} {{pagination.content_results.total}}</button>
  </div>

  {{! Search Tabs to select between products / content results}}
  <div class="search-tabs" data-search-tab="products">
    {{> components/product-lists/list-utils
      listtype=product_results
      pagination=pagination.product_results
    }}

    {{> components/product-lists/display
      listtype=product_results
      pagination=pagination.product_results
      quickshop=theme_settings.use_quickshop
    }}

    {{> components/search-brand-category-results}}
  </div>

  {{! Content Results}}
  <div class="search-tabs hidden" data-search-tab="other">

    {{! Minimal List Utils for Content Results}}
    <div class="product-list-utils">
      {{> components/product-lists/list-results-counter
        listtype=content_results
        pagination=pagination.content_results}}

      {{> components/product-lists/sort sort=pagination.sort}}
    </div>

    {{> components/other-search-results}}

  </div>

{{/if}}

components/product-lists/list-results-co:
{{!--
  Product List Results

  @param pagination   Pagination object which contains start and end numbers.
  @param listtype     Product list object which contains the items (and total available)
--}}

{{#if listtype.total_products}}
  <span class="product-list-results">
    {{#if pagination.start}}
      {{lang 'common.displaying_X_results_of' start=pagination.start finish=pagination.end total=listtype.total_products}}
    {{else}}
      {{lang 'common.displaying_X_results' total=listtype.total_products}}
    {{/if}}
  </span>
{{else}}{{! TODO: Remove this if data is consistent with products }}
  <span class="product-list-results">
    {{lang 'common.X_results' total=pagination.total}}
  </span>
{{/if}}

components/product-lists/list-utils:
{{!--
  Product List Utilities - toolbar to refine product lists

  @param listtype       The section this display applies to.
  @param pagination     The pagination object for this section (for sort / results)
--}}
{{#if listtype.products}}
  <div class="product-list-utils">

    <button class="filter-panel-toggle">
      {{> svg/filter}}
      {{> svg/ex}}
      {{lang 'faceted_search.refine'}}
    </button>

    <div class="product-list-refine">
      {{#each listtype.facets}}
        {{#if selected}}
          <a class="active-filter" href="{{remove_url}}" data-faceted-search-facet data-facet-remove>
            {{title}} {{> svg/ex}}
          </a>
        {{/if}}
      {{/each}}
    </div>

    {{> components/product-lists/list-results-counter
      listtype=listtype
      pagination=pagination}}

    {{> components/product-lists/sort sort=pagination.sort}}

    {{! Sidebar first (top of mobile) }}
    {{> components/product-lists/sidebar listtype=listtype}}
  </div>
{{/if}}

