components/product/price:
{{!
  Product Price - Sale vs Normal, With Tax vs Without

  @param this price object

  @param show_savings bool (optional)
         If true, show a label saying how much is saved from the full price.

  @param schema_org bool (optional)
         If false, will keep the schema.org meta data out of this price.
}}
{{#if price}}

  <span class="price {{> components/common/price-class}}"
    itemprop="offers"
    itemscope
    itemtype="http://schema.org/Offer">

    {{#if schema_org}}
      <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
      {{#if price.without_tax}}
        <meta itemprop="price" content="{{price.without_tax.value}}">
      {{else}}
        <meta itemprop="price" content="{{price.with_tax.value}}">
      {{/if}}
    {{/if}}

    {{#if price.with_tax}}
      <span class="price-withtax" data-price="with-tax">
        <span class="price-value" data-price-value="with-tax">
          {{price.with_tax.formatted}}
        </span>
        {{#if price.rrp_with_tax}}
          <span class="price-rrp" data-price-rrp="with-tax">{{price.rrp_with_tax.formatted}}</span>
        {{/if}}
        {{#if price.without_tax}}
          <span class="price-tax-label">{{lang 'product.including_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}

    {{#if price.without_tax}}
      <span class="price-withouttax" data-price="without-tax">
        <span class="price-value" data-price-value="without-tax">
          {{price.without_tax.formatted}}
        </span>
        {{#if price.rrp_without_tax}}
          <span class="price-rrp" data-price-rrp="without-tax">{{price.rrp_without_tax.formatted}}</span>
        {{/if}}
        {{#if price.with_tax}}
          <span class="price-tax-label">{{lang 'product.excluding_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}

    {{#if show_savings}}
      {{#if price.saved}}
        <span class="price-saved" data-price-saved>
          ({{lang 'product.you_save'}} {{price.saved.formatted}})
        </span>
      {{/if}}
    {{/if}}

  </span>
{{/if}}

components/product/product-grid-item:
{{!--
  Product Grid Item - As displayed within a grid listing of products.

  @param product object
    The product object to display, handed over from the loop

  @param compare bool
    Whether to show "compare" checkboxes.

  @param quickshop theme-setting
    Theme setting to enable / disable quickshop

  @param image-ratio theme-setting
    The image ratio for this grid to display at.
--}}

<article
  class="product-item{{#if compare}} product-item-compare{{/if}}{{#if quickshop}} product-item-quickshop{{/if}}">

  {{#if out_of_stock_message}}
    <span class="product-badge badge-sold-out">{{lang 'product.sold_out'}}</span>
  {{else if price.saved}}
    <span class="product-badge badge-sale">{{lang 'product.sale'}}</span>
  {{/if}}

  <div class="product-item-image">
    {{> components/common/image
      img=image
      size="productlist"
      ratio=image-ratio
      default=default-image
      image-ratio=image-ratio
    }}

    <div class="product-item-overlay">
      {{#if compare}}
        <label class="compare-checkbox-label" for="compare-{{id}}">
          <input
            class="compare-checkbox"
            id="compare-{{id}}"
            type="checkbox"
            data-compare-checkbox
            data-compare-id="{{id}}"
            data-compare-title="{{name}}"
            data-compare-url="{{url}}"
            data-compare-price="{{price}}"
            data-compare-thumbnail="{{getImage image 'thumb' (cdn default-image)}}">
          <span class="compare-label-text">
            {{lang 'core.product.compare'}}
          </span>
        </label>
      {{/if}}
    </div>
  </div>

  <div class="product-item-details">
    <h3 class="item-title"><a href="{{url}}">{{{name}}}</a></h3>
    {{> components/product/price price=price}}
  </div>

  {{#if quickshop}}
    <a class="button-quickshop" href="{{url}}" data-quick-shop="{{id}}">
      {{> svg/quickview}}{{lang 'common.quickview'}}
    </a>
  {{/if}}

</article>

components/reviews/ratings:
{{!--
  Display Rating as Series of Stars

  @param rating
    The Rating value to display.
--}}

<span class="rating-stars" aria-hidden="true">
  {{#for 1 5}}
    {{#if ../rating '>=' $index}}
      <svg class="star star-full">
        <use xlink:href="#icon-star-full">
      </svg>
    {{else}}
      <svg class="star star-empty">
        <use xlink:href="#icon-star">
      </svg>
    {{/if}}
  {{/for}}
</span>

