    </div>
  {{else}}
    <ul class="nav-menu">
      {{#if theme_settings.menu_sidebar '==' 'brands'}}
        {{> components/common/nav-menu menu=brands}}
      {{else if theme_settings.menu_sidebar '==' 'categories'}}
        {{> components/common/nav-menu menu=categories}}
      {{else}}
        {{> components/common/nav-menu menu=pages}}
      {{/if}}
    </ul>
  {{/if}}
</nav>

