components/content-header:
{{!--
  Main Content Header - Used on top of many pages, often containing breadcrumbs
  @param title        The title to be used in an h1 (optional)
--}}

<header class="content-header
  {{#unless theme_settings.invert_header}}content-header-tinted{{else}}contained{{/unless}}">
  <div class="header-wrap {{#unless theme_settings.invert_header}}contained{{/unless}}">
    {{> components/common/breadcrumbs breadcrumbs}}
    {{#if title}}<h1 class="page-title">{{{title}}}</h1>{{/if}}
  </div>
</header>

components/common/breadcrumbs:
{{!--
  Displayed in the content header and links to parent pages.
  Note: Each element is on same line with no extra white spacespace
--}}
{{!--
  NOTE: No breadcrumbs featured in this design, but they can be added easily
        by uncommenting this section.

  {{#if this}}
    <div class="breadcrumbs">
      {{#each this}}
        {{#unless @last}}
          <a class="breadcrumb" href="{{url}}">{{{name}}}</a> /
        {{else}}
          <span class="breadcrumb active" href="{{url}}">{{{name}}}</span>
        {{/unless}}
      {{/each}}
    </div>
  {{/if}}
--}}
