components/footer-navigation:
{{!--
  Display either the 'pages menu' or the 'categories menu'
  depending on the theme setting.
--}}

<ul class="nav-sitemap">
  {{#if theme_settings.menu_footer '==' 'brands'}}
    {{> components/common/nav-menu menu=shop_by_brand}}
  {{else if theme_settings.menu_footer '==' 'categories'}}
    {{> components/common/nav-menu menu=categories}}
  {{else if theme_settings.menu_footer '==' 'pages'}}
    {{> components/common/nav-menu menu=pages}}
  {{else}}
    {{> components/nav-sitemap}}
  {{/if}}
  {{#if theme_settings.show_sitemap}}
    <li><a href="{{urls.sitemap}}">{{lang 'common.sitemap_title'}}</a></li>
  {{/if}}
</ul>

components/common/nav-menu:
{{!--
  Main Navigation Menu: Loops over a menu object and displays the list
  @param menu   The menu object to loop over.
--}}
{{#each menu}}
  <li class="nav-menu-item{{#if children}} menu-dropdown{{/if}}" >
    <span class="nav-menu-item-wrapper"><a href="{{url}}">{{name}}</a></span>
    {{#if children}}
      <ul class="dropdown">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  <li>
                    <a href="{{url}}">{{name}}</a>
                  </li>
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}
