components/cart/mini-cart-content:
{{! Main Mini-Cart }}
<div class="mini-cart-content">

  {{#if cart.items.length}}

    {{! Display the cart items }}
    <ul>
      {{#each cart.items}}
        <li class="mini-cart-item">

          {{! Product Image }}
          <div class="mini-cart-item-image mini-cart-item-col">
            {{#if type '===' 'GiftCertificate'}}
              {{! Gift Card Graphic }}
              <svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px"
              	 viewBox="0 0 100 81.9" style="enable-background:new 0 0 100 81.9;" xml:space="preserve">
                <path style="fill:currentColor" d="M77.4,0.3L5.8,21.1c-4.3,1.2-6.7,5.7-5.5,10l12.3,42.3c1.2,4.3,5.7,6.7,10,5.5l71.6-20.8
                	c4.3-1.2,6.7-5.7,5.5-10L87.4,5.8C86.2,1.5,81.7-0.9,77.4,0.3z M84.1,6.8l2.6,8.8L48.1,26.8c-0.7-2.2-1.8-5.4-3.2-7.6
                	c0,0-7.6,1.7-11,5.6l-2.2-7.6L78.4,3.6C80.8,2.9,83.4,4.3,84.1,6.8z M6.8,24.5l17.4-5.1l2.4,8.1c-5.1-0.3-11.4,3.9-11.4,3.9
                	c0.4,1.5,1,3,1.6,4.5L6.2,39l-2.6-8.8C2.9,27.7,4.3,25.2,6.8,24.5z M15.9,72.5l-7.6-26l17.1-5c-0.8,2.5-2,5.5-3.6,9.1l5.2,9.4
                	c0,0,2.5-7.1,4.7-14.7l7.3,25.3l-17.4,5.1C19.2,76.3,16.6,74.9,15.9,72.5z M93.3,54.8L46.5,68.4l-8.3-28.5C45.9,46,55,52.5,55,52.5
                	L53,42c-3.4-2.3-6.2-4.3-8.4-6l44.3-12.9l7.6,26C97.1,51.5,95.7,54.1,93.3,54.8z"/>
                <ellipse style="fill:currentColor" cx="53.6" cy="79.3" rx="41.3" ry="2.6"/>
              </svg>
            {{else}}
              {{> components/common/image
                img=image
                size='thumb'
                default=../../theme_settings.default_image alt=image.alt
                not-lazy=true
              }}
            {{/if}}
          </div>

          {{! Product Qty, Name, Variant, and Price }}
          <div class="mini-cart-item-name mini-cart-item-col">
            <span class="mini-cart-item-qty-label">{{lang 'cart.qty' count=quantity}}</span>
            <a class="mini-cart-item-title" href="{{url}}" alt="{{name}}">{{name}}</a>
            {{#if options.length}} &ndash; {{#each options}}{{value}} {{/each}}{{/if}}<br>


            {{! Price}}
            <div class="mini-cart-item-price mini-cart-item-col {{#if total_discounted}}cart-item-price-discounted{{/if}}">{{{total.formatted}}}</div>
            {{#if total_discounted}}
              <div class="mini-cart-item-price mini-cart-item-col">{{total_discounted.formatted}}</div>
            {{/if}}
          </div>

          {{! Remove Link }}
          <div class="mini-cart-item-col">
            <a class="cart-item-remove" href="{{remove_url}}" data-mini-cart-remove="{{id}}">
              {{> svg/ex}}
            </a>
          </div>

        </li>
      {{/each}}
    </ul>

    {{! Totals / Links to checkout }}
    <div class="mini-cart-footer">
      <div class="mini-cart-footer-total">
        <div class="mini-cart-footer-total-label">{{lang 'cart.grand_total'}}</div>
        <div class="mini-cart-footer-total-value">{{cart.grand_total.formatted}}</div>
      </div>
      <div class="mini-cart-footer-buttons">
        <span class="mini-cart-view-cart-link"><a href="{{urls.cart}}">{{lang 'cart.view_cart'}}</a></span>
        <a href="{{urls.checkout.single_address}}" class="button mini-cart-checkout-button">{{lang 'cart.checkout'}}</a>
      </div>
    </div>
  {{else}}
    {{! Empty Text }}
    <p class="cart-preview-empty">{{lang "cart.empty_cart"}}</p>
  {{/if}}

</div>
