components/common/pagination:
{{!--
  Pagination: Previous / Next buttons and a list of links to other pages.
  @param this Object containing links, count, etc
--}}
<nav class="pagination">
  {{#if previous}}
    <a class="pagination-item pagination-previous" href="{{previous}}" data-faceted-search-facet>
      <span class="visually-hidden">{{lang 'common.pagination.previous'}}</span>
    </a>
  {{/if}}

  {{#each links}}
    <a
      class="pagination-item{{#if number "==" ../current}} pagination-current{{/if}}"
      href="{{url}}"
      data-faceted-search-facet>
      {{number}}
    </a>
  {{/each}}

  {{#if next}}
    <a class="pagination-item pagination-next" href="{{next}}" data-faceted-search-facet>
      <span class="visually-hidden">{{lang 'common.pagination.next'}}</span>
    </a>
  {{/if}}
</nav>

components/other-search-results:
{{!-- Showing individual blog and page results on the search page --}}

{{#each content_results}}
  <article class="search-result content-result">
    <h3 class="search-result-title"><a href="{{url}}">{{title}}</a></h3>
    <p>{{content}}</p>
    <p class="content-result-type">{{type}}</p>
  </article>
{{/each}}

{{> components/common/pagination pagination.content_results}}
